SELECT t_video_course.video_course_id, t_video_course.video_course_name, t_video_course.video_title, t_video_course.video_info, t_video_course.type_id, t_video_course.video_course_url, t_video_course.video_course_img_url, t_video_course.upload_user_id, t_video_course.upload_time, t_video_course.teacher_user_id, t_video_course.watch_number, t_video_course.video_price, t_video_course.course_type_id FROM t_video_course
WHERE t_video_course.type_id = #{typeId}
ORDER BY t_video_course.video_price DESC

